import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "CustomerServlet", value = "")
public class CustomerServlet extends HttpServlet {
    private List<Customer> customerList = new ArrayList<>();

    public void init() {
        customerList.add(new Customer("Đạt", "26/09/1999", "Huế",
                "https://scontent.fdad1-4.fna.fbcdn.net/v/t1.6435-9/158909585_1352562481788340_2644646544529071670_n.jpg?_nc_cat=100&ccb=1-7&_nc_sid=09cbfe&_nc_ohc=j4aIVdXa_mgAX-TigOn&tn=1oqwERuHMXqCiusA&_nc_ht=scontent.fdad1-4.fna&oh=00_AfBiAFRajepDFS-JGbBjONBnR0x2Qc-LVyXq474YR5hNkw&oe=640D6A1F"));
        customerList.add(new Customer("Đồng", "09/03/1999", "Quảng Trị",
                "https://images.pexels.com/photos/1442297/pexels-photo-1442297.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"));
        customerList.add(new Customer("Hi", "07/07/2003", "Đà Nẵng",
                "data:image/jpeg;base64,"));
        customerList.add(new Customer("Khải", "01/12/1999", "Quảng Bình",
                "https://scontent.fsgn2-5.fna.fbcdn.net/v/t31.18172-8/15326268_1816226141990948_3564166672928073271_o.jpg?stp=c0.78.304.304a_dst-jpg_p180x540&_nc_cat=104&ccb=1-7&_nc_sid=da31f3&_nc_ohc=aNqztMe6OzkAX86pQYR&tn=1oqwERuHMXqCiusA&_nc_ht=scontent.fsgn2-5.fna&oh=00_AfBq94gJ3gRQQmm5q93Qq5aOlLz0tK3oOe6uTQRE6sw-sA&oe=640D8044"));
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        request.setCharacterEncoding("UTF-8");
        response.setContentType("text/html;charset=UTF-8");
        request.setAttribute("customerList", customerList);
        request.getRequestDispatcher("/index.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
    }
}
